#!/bin/bash

# I am really sorry for anyone who has to work with this, including myself and Kat.

echo "<h1>Updating the Magical Pony</h1>" > /var/www/index.html

echo "<h2>" >> /var/www/index.html
echo `date` >> /var/www/index.html
echo "</h2>" >> /var/www/index.html

echo "<img src='data:image/png;base64,'/>" >> /var/www/index.html

pushd ~/host/

git pull

for branch in `git branch -a | grep remotes | grep -v HEAD | grep -v master`;
do
    git checkout $(basename "$branch")
    git pull
    mkdir -p /srv/clones/$(basename "$branch")
    git archive $(basename "$branch") \
        | tar -xC /srv/clones/$(basename "$branch")
    cp ~/default /etc/apache2/sites-enabled/$(basename "$branch").conf
    perl -p -i -e "s/MAGICALPONY/$(basename "$branch")/g" \
         /etc/apache2/sites-enabled/$(basename "$branch").conf
done

popd

echo "<h1>And we're done!</h1>" >> /var/www/index.html

echo "<h2>" >> /var/www/index.html
echo `date` >> /var/www/index.html
echo "</h2>" >> /var/www/index.html

service apache2 restart

